import React, {useState} from 'react';
import './App.css'


function App() {

  const apiKey = '0ca0cf2b477cf81859c0a0e548b04dd3'
  const [data, setData] = useState([{}])
  const [name, setName]= useState("")

  const getData = (event) => {
    if (event.key == "Enter"){
      fetch('https://rest.bandsintown.com/artists/{name}/?app_id=0ca0cf2b477cf81859c0a0e548b04dd3').then(
        response => response.json()
      ).then(
        data => {
          setData(data)
          setName("")
        }
      )
    }
  }

  return (
    <div className="container">
      <input className="input" placeholder="Enter Name..."
      onChange={e => setName(e.target.value)}
      value={name}
      onKeyPress={getData}
       />

    <div>
      <p>{data.id}</p>
      </div>  

    </div>
  
  );
}

export default App;
