import React from "react";
import "./App.css";

export default class FetchRandomUser extends React.Component {
  state = {
    loading: true,
    person: null
  };

  async componentDidMount() {
    const url = "https://rest.bandsintown.com/artists/Justin%20Bieber/?app_id=0ca0cf2b477cf81859c0a0e548b04dd3";
    const response = await fetch(url);
    const data = await response.json();
    this.setState({ person: data, loading: false });

  }

  render() {
    if (this.state.loading) {
      return <div>loading...</div>;
    }

    if (!this.state.person) {
      return <div>didn't get a person</div>;
    }

    return (
      <div className="Component">
       <h1>{this.state.person.name}</h1>
       <img src={this.state.person.thumb_url}></img>
       <p>website: {this.state.person.links[1].url}</p>
       <p>facebook: {this.state.person.links[2].url}</p>
       <p>newsletter: {this.state.person.links[3].url}</p>
       <p>instagram: {this.state.person.links[4].url}</p>
       <p>twitter:{this.state.person.links[5].url}</p>
       <p>Spotify: {this.state.person.links[6].url}</p>
       <a href={this.state.person.links[7]} target="_blank">Spotify</a>
      </div>
    );
  }
}
