import React from "react";
import {useState} from 'react';
import "./App.css";

export default class App extends React.Component {
  state = {
    loading: true,
    person: null
  };

  async componentDidMount() {
    const url = "https://rest.bandsintown.com/artists/Justin%20Bieber/?app_id=0ca0cf2b477cf81859c0a0e548b04dd3";
    const response = await fetch(url);
    const [searchValue,setSearchValue]=useState('');
    const data = await response.json();
    this.setState({ person: data, loading: false });

  }


  render() {

    const [searchValue,setSearchValue]=useState('');
   
    const setQuery=(e)=>{
      setSearchValue(e.target.value)
   }
    if (this.state.loading) {
      return <div>loading...</div>;
    }

    if (!this.state.person) {
      return <div>not found !</div>;
    }

    return (
      <div className="Component">
       <input placeholder="search artist" onKeyPress={setQuery}></input>
       <h1>{this.state.person.name}</h1>
       <img src={this.state.person.thumb_url}></img>
       <h2>RÃ©seaux sociaux:</h2>
       <a href={this.state.person.links[1].url} target="_blank">Site web</a>
       <br></br>
       <a href={this.state.person.links[2].url} target="_blank">Facebook</a>
       <br></br>
       <a href={this.state.person.links[3].url} target="_blank">Newsletter</a>
       <br></br>
       <a href={this.state.person.links[4].url} target="_blank">Instagram</a>
       <br></br>
       <a href={this.state.person.links[5].url} target="_blank">Twitter</a>
       <br></br>
       <a href={this.state.person.links[6].url} target="_blank">Spotify</a>
      </div>
    );
  }
}
