import React, {Component} from 'react';
import './App.css';


class App extends Component {

  state = {
    post: {}
  }

  componentDidMount(){
    fetch('https://rest.bandsintown.com/artists/Justin%20Bieber/?app_id=0ca0cf2b477cf81859c0a0e548b04dd3')
    .then((response) =>{
      return response.json()
    })

    .then((result) => {
      this.setState({post: result })
    })
    
    const image = this.state.post.thumb_url;

  }
  render(){
    return(
      <div className="App">
        <h1>Informations:</h1>
       <img src="{image}"></img>
             </div>
    );
  }
}

export default App;
